#' @return A modified Vega-Lite Spec
#' @export
vl_encode_url <- function(spec, .object = NULL, field = NULL, type = NULL, aggregate = NULL, band = NULL, bin = NULL, condition = NULL, format = NULL, formatType = NULL, labelExpr = NULL, timeUnit = NULL, title = NULL, value = NULL) {
  obj <- .modify_args(NULL, NULL)
  .add_encoding(spec, obj, "#/definitions/Encoding/properties/url" , encoding = "url")
}

