vl_aggregate <- function(spec, .object = NULL, aggregate = NULL, groupby = NULL) {
  obj <- .modify_args(NULL, NULL)
  .add_transform(spec, obj, "#/definitions/AggregateTransform" , .trans = "aggregate")
}

