#' @return A modified Vega-Lite Spec
#' @export
vl_facet_column <- function(spec, .object = NULL, field = NULL, type = NULL, aggregate = NULL, band = NULL, bin = NULL, header = NULL, sort = NULL, timeUnit = NULL, title = NULL) {
  obj <- .modify_args(NULL, NULL)
  .add_facet_column(spec, obj, "#/definitions/FacetFieldDef")
}

